mapper.select:
select * 
from lineorder,user user1
where user1.userId=lineorder.salesmanId and lineorder.orderDate like #{date,jdbcType=VARCHAR}
and lineorder.salesmanId=#{salesmanId,jdbcType=VARCHAR}

      	select count(*)
		from lineorder
		where lineProductId = #{lineProductId,jdbcType=INTEGER}

      	select count(*)
		from lineorder,user user1
		where user1.userId=lineorder.salesmanId 
		and lineorder.salesmanId=#{salesmanId,jdbcType=VARCHAR}
		and lineorder.lineOperatorId=#{lineOperatorId,jdbcType=VARCHAR}
		and lineorder.visaOperatorId=#{visaOperatorId,jdbcType=VARCHAR}
		and lineorder.signOperatorId=#{signOperatorId,jdbcType=VARCHAR}
		and ${role} in (select userId from user where managerId=#{managerId,jdbcType=VARCHAR})
