mapper.select:
select count(*) from user 
where 1=1
and userName like CONCAT('%',#{userName,jdbcType=VARCHAR},'%')
and roleId = #{roleId,jdbcType=INTEGER}
and lineRoleId = #{lineRoleId,jdbcType=INTEGER}
order by postDt desc

select u1.*,u2.userName as managerName  
FROM user u1 
LEFT JOIN user u2 ON u1.managerId=u2.userId  
where 1=1
and u1.userName like CONCAT('%',#{user.userName,jdbcType=VARCHAR},'%')
and u1.roleId = #{user.roleId,jdbcType=INTEGER}
and u1.lineRoleId = #{user.lineRoleId,jdbcType=INTEGER}
order by  enable desc,postDt desc 
limit #{start,jdbcType=INTEGER},#{pageCount,jdbcType=INTEGER}

select count(*) from user 
where 1=1
and userName like CONCAT('%',#{userName,jdbcType=VARCHAR},'%')
and roleId = #{roleId,jdbcType=INTEGER}
 and lineRoleId = #{lineRoleId,jdbcType=INTEGER}
order by postDt desc

select u1.*,u2.userName as managerName  
FROM user u1 
LEFT JOIN user u2 ON u1.managerId=u2.userId  
where 1=1
and u1.userName like CONCAT('%',#{user.userName,jdbcType=VARCHAR},'%')
and u1.roleId = #{user.roleId,jdbcType=INTEGER}
 and u1.lineRoleId = #{user.lineRoleId,jdbcType=INTEGER}
order by  enable desc,postDt desc 
limit #{start,jdbcType=INTEGER},#{pageCount,jdbcType=INTEGER}

select * from user 
where userId = #{roleId,jdbcType=VARCHAR}
and enable = 1

mapper.update:
update user
set pwd = #{pwd,jdbcType=VARCHAR}
where userId = #{userId,jdbcType=VARCHAR}
